(window.webpackJsonp=window.webpackJsonp||[]).push([[123],{449:function(t,s,a){"use strict";a.r(s);var n=a(19),r=Object(n.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("p",[t._v("体克用了解")]),t._v(" "),s("h2",{attrs:{id:"爱上发上"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#爱上发上"}},[t._v("#")]),t._v(" 爱上发上")]),t._v(" "),s("p",[t._v('梅花易数上主卦、互卦、变卦是三分法。在原书上将变卦作为“结果"是就“开始、过程、结果”这个三分方面得出的。比如我们在预测的时候，主卦可以是早年，互卦是中年，变卦是晚年。主互变是个三分法，而不是一个“一成不变”的结果模式。\n简单来说，梅花易数的预测分为两个层面：第一是对于结果的预测，第二是对于情形的预测。比如我要测这个事情能否能办成，就两个答案，要么成要么不成。这个要看“体用变”的关系，要结合旺衰和五行的力量对比，对体有利一般都是能成的。那么变卦对体卦有利，当然是一个好的情况。第二是我要看这个事情是什么样子的，过程是怎么发展的，成或是不成是因为什么，这当然是对情形的预测。这里牵涉到了卦象的解读问题。下面我用自己的预测卦例简要说明下。\n'),s("img",{attrs:{src:"/images/15856504557634.jpg",alt:"-w395"}})]),t._v(" "),s("h2",{attrs:{id:"爱上发上-2"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#爱上发上-2"}},[t._v("#")]),t._v(" 爱上发上")]),t._v(" "),s("p",[t._v("此卦为癸未月占，是一女子求测老公财运，还有他们之间感情的。我就其中关于测她老公财运的部分断语公布下，并指出卦理。\n女子求测，体卦为自己，用卦为自己老公。\n1.老公现在想换生意行业。\n2.老公人际关系出了问题。\n3.现在赋闲在家打算投资。\n未月巽卦为囚状态，且全卦金多木少，并无坎卦通关。巽卦的意象全部呈现消极。巽为思想，为想法，初爻动，有“釜底抽薪”之意。所以断现在赋闲在家。上为震卦，下为巽卦，震为动，巽为财，也为思绪，就是“动了思绪”，变卦是乾卦，是钱财的意思。就是“动脑子想去赚钱”。震为手，巽为股，名为“手足”，也就是朋友，即人际关系出现问题了。震兑对冲，兑是2，所以是两个人对他有意见了，兑下为乾卦，所以对方两人都是老板。\n嗯，我就简单说到这，不难发现，要想断对卦第一是要熟悉卦象的（也就是八卦万物类象），第二是对于五行流转的把握，能量的流向看清。")])])}),[],!1,null,null,null);s.default=r.exports}}]);